import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class ClassesList extends Component {
    constructor(props) {
        super(props);

        this.deleteClasses = this.deleteClasses.bind(this);

        this.state = {Classes: []};
    }

    componentDidMount() {
        axios.get('http://localhost:5000/classes/')
            .then(response => {
                this.setState({ classes: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    deleteClasses(id) {
        axios.delete('http://localhost:5000/classes/'+id)
            .then(res => console.log(res.data));

        this.setState({
            classes: this.state.classes.filter(el => el._id !== id)
        })
    }

    render() {
        return(
            <div>
                <p>You are on the Classes List component!</p>
            </div>
        )
    }
}
