import React, { Component } from 'react';
import { isThisHour } from 'date-fns/esm';

export default class CreateUsers extends Component {
    constructor(props) {
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            username : '',
        }  
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const user ={
            username: this.state.username,
        }

        console.log(user);

        this.setState({
            username:''
        })
    }

    render() {
        return(
            <div>
                <h3>Create New Classes List</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <select ref="userInput" required className="form-control" value ={this.state.username}
                        onChange = {this.onChangeUsername}>
                        {
                            this.state.users.map(function(user) {
                                return <option key={user}
                                value= {user}>{user}
                                </option>;
                            })
                        }
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input type="text"
                        required
                        className="form-control"
                        value={this.state.description}
                        onChange={this.onChangeDescription}/>
                    </div>
                    <div className="form-group">
                        <label>Duration: </label>
                        <input type="text"
                        required
                        className="form-control"
                        value={this.state.duration}
                        onChange={this.onChangeDuration}/>
                    </div>
                    <div className="form-group">
                        <label>Date: </label>
                        <DatePicker 
                            selected={this.state.date}
                            onChange={this.onChangeDate}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create Class List" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
